mapper.update:
update step_tbl
set stepNo= #{stepNo},
stepDescription=#{stepDescription},
stepImage = #{stepImage},
tip = #{tip}
where sno = #{sno}

update ingredient_tbl
set ingreName= #{ingreName},
ingreMeasure=#{ingreMeasure},
ingreTypeNo = #{ingreTypeNo},
ingreType = #{ingreType}
where ino = #{ino}

mapper.select:
select INGRENAME from INGREDIENT_TBL where bno = #{bno}
and INGRENAME not in (select INGRENAME from own_tbl where
id='로그인한 사용자')

select INGRENAME from INGREDIENT_TBL where bno = #{bno}
and INGRENAME in (select INGRENAME from own_tbl where id='
로그인한 사용자')
