mapper.select:

select rno, bno, reply, id, replyDate, updateDate
from
	(
	select /*+INDEX(tbl_reply idx_reply) */
	rownum rn, rno, bno, reply, id, replyDate, updateDate
	from reply_tbl
	where bno=#{bno}
	and rno>0
	and rownum <= #{cri.pageNum} * #{cri.amount}
	)
	where rn > (#{cri.pageNum} -1) * #{cri.amount}
